tinymce.PluginManager.add('uploadimage', function (editor) {

    function selectLocalImages() {
        
        var dom = editor.dom;
       
        var input_f = $('<input type="file" name="image" accept="image/jpg,image/jpeg,image/png,image/gif" multiple="multiple">');
        input_f.on('change', function () {
            var form = $("<form/>",
                {
                    action: editor.settings.upload_image_url, //设置上传图片的路由，配置在初始化时
                    style: 'display:none',
                    method: 'post',
                    enctype: 'multipart/form-data'
                }
            );
            form.append(input_f);
            //ajax提交表单
            form.ajaxSubmit({
                beforeSubmit: function () {
                    return true;
                },
                success: function (data) {                   
                    if (data) {
                        var jsonarray = $.parseJSON(data);
                        if (jsonarray.success) {
                            editor.focus();
                            //data.forEach(function (src) {
                            //    alert("data" + src);
                            editor.selection.setContent(dom.createHTML('img', { src: jsonarray.imageUrl }));
                        //})
                        }  
                       
                    }
                },
                error: function (value) {
                    alert("失败" + value);
                }
            });
        });

        input_f.click();
    }

    editor.addCommand("mceUploadImageEditor", selectLocalImages);

    editor.addButton('uploadimage', {
        icon: 'image',
        tooltip: 'Insert\/edit image',
        onclick: selectLocalImages
    });

    editor.addMenuItem('uploadimage', {
        icon: 'image',
        text: 'Insert\/edit image',
        context: 'tools',
        onclick: selectLocalImages
    });
});